sap.m.Image.prototype.setSrc = function(sSrc){
	if(sSrc === this.getSrc()){
		return;
	}
	this.setProperty("src", sSrc, true);
	
	var oDomRef = this.getDomRef();
	if(oDomRef){
		this.$().attr("src", this._getDensityAwareSrc());
	}
};

